<script setup>

import { FwbBadge } from 'flowbite-vue'
import AccountCard from '@/components/AccountCard.vue'
import { onMounted, onUnmounted, ref } from 'vue'

const showAccountCard = ref(false);


function toggleAccountCard() {
  showAccountCard.value = !showAccountCard.value;
}

function closeAccountCard() {
  showAccountCard.value = false;
}

// Close AccountCard when clicking outside
function handleClickOutside(event) {
  const accountCard = document.querySelector('.account-card');
  if (accountCard && !accountCard.contains(event.target)) {
    closeAccountCard();
  }
}

onMounted(() => {
  document.addEventListener('click', handleClickOutside);
});

onUnmounted(() => {
  document.removeEventListener('click', handleClickOutside);
});

</script>

<template>
  <div class="relative flex flex-col items-center justify-center">
    <!-- Logo -->
    <img src="/images/zeuxis.png" alt="Flowbite logo" class="w-30 h-20" />
    <!-- Text -->
    <fwb-badge class="bg-white text-black" size="xl" @click="toggleAccountCard">
      <p class="mr-2">Account</p>
      <svg
        width="20"
        height="23"
        viewBox="0 0 20 23"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
        xmlns:xlink="http://www.w3.org/1999/xlink"
      >
        <rect width="20" height="23" fill="url(#pattern0_58_1349)" />
        <defs>
          <pattern
            id="pattern0_58_1349"
            patternContentUnits="objectBoundingBox"
            width="1"
            height="1"
          >
            <use
              xlink:href="#image0_58_1349"
              transform="matrix(0.00193603 0 0 0.0016835 -0.00820707 0)"
            />
          </pattern>
          <image
            id="image0_58_1349"
            width="525"
            height="594"
            xlink:href="data:image/png;base64,"
          />
        </defs>
      </svg>
      <p class="ml-2">1378</p>
    </fwb-badge>

    <div v-if="showAccountCard">
      <div
        class="fixed inset-0 bg-white bg-opacity-25 z-0"
        @click="closeAccountCard"
      ></div>

    <AccountCard
      class="absolute top-10 right-132.5 z-10 max-w-xs sm:max-w-md md:max-w-lg lg:max-w-xl"
      v-if="showAccountCard"
      @close="toggleAccountCard"
    />
  </div>
  </div>
</template>
