<script setup>
import { onMounted, ref } from 'vue';
import 'tui-image-editor/dist/tui-image-editor.css';
import ImageEditor from 'tui-image-editor';
import DefaultLayout from '@/layout/DefaultLayout.vue';

const editorContainer = ref(null);
let imageEditor = null;

const props = defineProps({
  imageSrc: String,
});

const image = "data:image/webp;base64,";

onMounted(() => {
  if (editorContainer.value) {
    imageEditor = new ImageEditor(editorContainer.value, {
      includeUI: {
        loadImage: { path: image, name: 'SampleImage',
          width: '100%',
          height: '100%',
        },
        theme: {},
        menu: ['crop', 'flip', 'rotate', 'draw', 'shape', 'icon', 'text', 'mask', 'filter'],
        initMenu: 'filter',
        uiSize: { width: '100%', height: '1000px' },
        menuBarPosition: 'bottom'
      },
      cssMaxWidth: 700,
      cssMaxHeight: 700,
      selectionStyle: { cornerSize: 10, rotatingPointOffset: 20 }
    });
    console.log('Editor Loaded');
  }
});

const downloadImage = () => {
  if (imageEditor) {
    const editedImage = imageEditor.toDataURL();
    console.log('Edited Image Data URL:', editedImage);
  }
};
</script>

<template>

<DefaultLayout> 
  <div class="tui-editor-container mt-3">
    <div ref="editorContainer"></div>
  </div>
  <button @click="downloadImage" class="download-btn">Download</button>
</DefaultLayout>
</template>


<style>
.tui-editor-container {
  width: 90%;
  height: 100px;
}
</style>
